// Determine if the fragment lies inside a grid pixel (square)
bool isInsidePixel(vec2 uv, int x, int y) {
    // Compute bounds in aspect-corrected space
    vec2 minP = gridToUV(x, y);
    vec2 maxP = gridToUV(x + 1, y + 1);

